search.script:
function viv(q)
{
  var suffix;
  var result;
  var se = "Yahoo,MSN,Fast,Netscape,OD,Excite,Looksmart,AskJeeves";  // Web Search

  if (q == "")
  {
    openSearchWindow("http://vivisimo.com/");
  }
  else
  {
    // Check for / suffix, 
    if (result = q.match(/^(.*)\/(\w+)/))
    {
      suffix = result[2];
      q = result[1];
      switch ( suffix.toLowerCase() )
      {
      case "tn":
      case "topnews":
        se = "CNN,NYTimes,YahooNews,WorldNews";
        break;
      case "tb":
      case "topbus":
        se = "CNNfn,YahooBizNews";
        break;
      case "tt":
      case "toptech":
        se = "Wired,YahooTechNews";
        break;
      case "ts":
      case "topsports":
        se = "YahooSportNews,CNNSI";
        break;
      case "yaa":
      case "yahooauctions":
        se = "YahooAuctions";
        break;
      case "ebay":
        se = "Ebay";
        break;
      case "yas":
      case "yahooshop":
        se = "YahooShopping";
        break;
      case "pubmed":
        se = "PubMed";
        break;
      case "usgov":
        se = "FirstGov";
        break;
      case "patent":
      case "delphion":
        se = "Delphion";
        break;
      case "gorp":
        se = "Gorp";
        break;
      case "buscom":
      case "business":
        se = "Business";
        break;
      case "brit":
      case "britannica":
        se = "Britannica";
        break;
      case "yahoo":
        se = "Yahoo";
        break;
      case "msn":
        se = "MSN";
        break;
      case "fast":
        se = "Fast";
        break;
      case "netscape":
        se = "Netscape";
        break;
      case "od":
        se = "OD";
        break;
      case "excite":
        se = "Excite";
        break;
      case "dh":
      case "directhit":
        se = "DirectHit";
        break;
      case "ls":
      case "looksmart":
        se = "Looksmart";
        break;
      case "aj":
      case "askjeeves":
        se = "AskJeeves";
        break;
      case "lycos":
        se = "Lycos";
        break;
      case "aol":
        se = "AOL";
        break;
      case "hb":
      case "hotbot":
        se = "HotBot";
        break;
      case "cnn":
        se = "CNN";
        break;
      case "nyt":
      case "nytimes":
        se = "NYTimes";
        break;
      case "yn":
      case "yahoonews":
        se = "YahooNews";
        break;
      case "wn":
      case "worldnews":
        se = "WorldNews";
        break;
      case "bbc":
        se = "BBC";
        break;
      case "sfg":
      case "sfgate":
        se = "SFGate";
        break;
      case "cnnfn":
        se = "CNNfn";
        break;
      case "ybn":
      case "yahoobiznews":
        se = "YahooBizNews";
        break;
      case "wired":
        se = "Wired";
        break;
      case "ytn":
      case "yahootechnews":
        se = "YahooTechNews";
        break;
      case "ysn":
      case "yahoosportnews":
        se = "YahooSportNews";
        break;
      default:
        se = "Yahoo,MSN,Fast,Netscape,OD,Excite,Looksmart,AskJeeves";  // Web Search
        break;
      }
    }

    document.vivisimof.query.value=q;
    document.vivisimof.se.value=se;
    submitForm(vivisimof);
  }
}
