search.script:
function wotsit(q)
{
  if( nullArgs("wotsit", q) )
    return false;

  var args = parseArgs(q, "all");
  if( q == "" )
    openSearchWindow("http://www.wotsit.org/");
  else if( args.switches.length == 0 )
  {
    document.wotsitsearch.target.value = args.q;
    submitForm(wotsitsearch); 
  }
  else
  {
    nullArgs("wotsit","?");
  }
}
