search.script:
function wpfr(q)
{
  var args = parseArgs(q, "prenom, adresse, localite, departement");
  if( nullArgs("wpfr", args.q) )
    return;

  document.wpfrf.prenom.value = "";
  document.wpfrf.voieLibelle.value = "";
  document.wpfrf.localiteLibelle.value = "";
  document.wpfrf.depRegLibelle.value = "";

  for (var iSwitch = 0; iSwitch < args.switches.length; iSwitch++)
  {
    switch( args.switches[iSwitch].name )
    {
      case "prenom":
        document.wpfrf.prenom.value = args.switches[iSwitch].value;
        break;
      case "adresse":
        document.wpfrf.voieLibelle.value = args.switches[iSwitch].value;
        break;
      case "localite":
        document.wpfrf.localiteLibelle.value = args.switches[iSwitch].value;
        break;
      case "departement":
        document.wpfrf.depRegLibelle.value = args.switches[iSwitch].value;
        break;
      default:
        break;
    }
  }

  document.wpfrf.nom.value = args.q;

  submitForm(wpfrf);
}
