search.script:
    function yy(q){
	//if (nullArgs("yy",q)){return false;}
	var yyids = yyid;
	try{
	  eval("var yyids = " + q + ";");
	}catch(e){
	  var yyids = yyid;
	}
	try{
	  document.yyf.login.value = yyids[0];
	  document.yyf.passwd.value = yyids[1];
	  if (yyids.length>1){
	    document.yyf['.done'].value = yyids[2];
	  }
	}catch(e){}
	submitForm(yyf);
    }
