search.script:
function zdn (q)
{
  if (q == "")
  {
    openSearchWindow(" http://www.zdnet.com/downloads");
  }
  else
  {
    var result = q.match(/^(.*)\/(\w+)/)
    var suffix;
    if (result)
    {
      suffix = result[2];
      q = result[1];
      switch(suffix.toLowerCase())
      {
      case "pc":
        document.downloadform.b.value="";
        document.downloadform.Utext.value=q;
        submitForm(downloadform);
        break;
      case "mac":
        document.downloadform.b.value="mac";
        document.downloadform.Utext.value=q;
        submitForm(downloadform);
        break;
      case "linux":
        document.downloadform.b.value="zdlinux";
        document.downloadform.Utext.value=q;
        submitForm(downloadform);
        break;
      case "palm":
        document.downloadform.b.value="zdpalm";
        document.downloadform.Utext.value=q;
        submitForm(downloadform);
        break
      case "ce":
        document.downloadform.b.value="zdce";
        document.downloadform.Utext.value=q;
        submitForm(downloadform);
        break;
      case "zd":
        document.zdform.Utext.value=q;
        submitForm(zdform);
        break;
      case "web":
        document.mainform.Utext.value=q;
        submitForm(mainform);
        break;
      default:
        document.downloadform.b.value="";
        document.downloadform.Utext.value=q;
        submitForm(downloadform);
        break;
      }
    }
    else
    {
      document.downloadform.b.value="";
      document.downloadform.Utext.value=q;
      submitForm(downloadform);
    }
  }
}
