search.script:
 function zdn(q)
 {
   var args = parseArgs(q, "all, mac, ce, linux, palm, web");
   if (q == "")
     openSearchWindow(" http://www.zdnet.com/downloads/");
   else if( args.switches.length == 1 )
   {
     switch( args.switches[0].name )
     {
     case "mac":
       document.zdn4f.Utext.value = args.q;
       submitForm(zdn4f);
       break;
     case "linux":
       document.zdn7f.Utext.value = args.q;
       submitForm(zdn7f);
       break;
     case "palm":
       document.zdn5f.Utext.value = args.q;
       submitForm(zdn5f);
       break
     case "ce":
       document.zdn6f.Utext.value = args.q;
       submitForm(zdn6f);
       break;
     case "all":
       document.zdn1f.Utext.value = args.q;
       document.zdn1f.Uch.value = '';
       submitForm(zdn1f);
       break;
     case "web":
       document.zdn2f.Utext.value = args.q; 
       submitForm(zdn2f);
       break;
     default:
       document.zdn3f.Utext.value = args.q;
       submitForm(zdn3f);
     }
   }
   else
   {
     document.zdn3f.Utext.value = args.q;
     submitForm(zdn3f);
   }
}
